            this.init().then(() => {
                resolve();
            }).catch(() => {
                reject();
            });
        });
    }
    async init() {
        const keyPair = await rsa.generateKeys();
        this.privateKey = keyPair.privateKey;
        this.publicKey = keyPair.publicKey;
