import { __decorate } from "tslib";
import { Component } from '@angular/core';
import * as bc from 'bigint-conversion';
let VotosComponent = class VotosComponent {
    constructor(votosService) {
        this.votosService = votosService;
    }
    //Hacer variables para guardar los valores de la PublicKey
    //Recibir los valores de la PublicKey (HECHO)
    //Utilizarlos para crear la funcion de encrypt y verify
    async ngOnInit() {
    }
    //Crear la funcion de encryptar con los valores de la clave publica (HECHO)
    async encrypt(mensaje) {
        const mensajeBigint = bc.textToBigint(mensaje);
        const cifrado = this.votosService.serverPublicKey.encrypt(mensajeBigint);
        console.log(bc.bigintToHex(cifrado));
        const plaintextHex = (await this.votosService.decrypt(bc.bigintToHex(cifrado))).message;
        console.log(bc.bufToText(bc.hexToBuf(plaintextHex)));
    }
    async sign(mensaje) {
        const mensajeBigint = bc.textToBigint(mensaje.value);
        const signed = this.votosService.privateKey.sign(mensajeBigint);
        (await this.votosService.verify(bc.bigintToHex(signed), this.votosService.publicKey)).subscribe((res) => {
            console.log(res);
        });
    }
};
VotosComponent = __decorate([
    Component({
        selector: 'app-votos',
        templateUrl: './votos.component.html',
        styleUrls: ['css/votos.component.css',
            // 'css/icons.css',
            'css/responsee.css',
            'css/template-style.css'
        ]
    })
], VotosComponent);
export { VotosComponent };
